
public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		
		System.out.println(a);//10
		
		a+=10;
		System.out.println(a);//20
		
		a-=10;
		System.out.println(a);//10
		
		a*=10;
		System.out.println(a);//100

	}

}
