
public class String3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1="Selenium";
		String str2=" Testing";
		System.out.println(str1+str2);//Selenium Testing
		System.out.println("Selenium"+(1+1));//Selenium 2
		System.out.println("Selenium"+1+1);//Selenium 1 1
		System.out.println(1+1+ "Selenium");//2 Selenium
		System.out.println("1"+1+ "Selenium");//1 1 Selenium
		System.out.println("Selenium"+"");
		System.out.println("  "+"Selenium");

	}

}
