
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char grade='A';
		
		switch (grade){
		
		case 'A':
			System.out.println("Excelent");
			//break;
			
		case 'X':
			System.out.println("Good");
			//break;
			
		default:
			System.out.println("Not");
		}
				

	}

}
